import React, {Component} from "react";
import ReactTimerStopwatch from './Timer/ReactTimerStopwatch';


export default class MeetingTime extends Component {
    constructor(props) {
        super(props);
        this.state = {
            displayHours: (props.hours !== 0),
        }
    }

    render() {
        return (
            <div>
                <ReactTimerStopwatch
                        isOn={this.props.isOn}
                        watchType="timer"
                        displayCircle={false}
                        finishEvent={this.props.finishEvent}
                        color="white"
                        hintColor="white"
                        displayHours={this.state.displayHours}
                        fromTime={new Date(0, 0, 0, this.props.hours, this.props.minutes, this.props.seconds, 0)}/>
            </div>

        )
    }
}
