import React, {Component} from "react";
import formationId from "../utils/FormationId";
import Style from "./UbUt.module.scss";

export default class MajFormation extends Component {

    render() {
        return (
            <div className="card-group center container">
                {this.props.formations.map((f, index) =>
                {if (f !== 0){
                    return (<div className={Style.styleF}>
                        <div className="card-header text-center bg-light">
                            <h2>{f !== 0 ? formationId(index) : null}</h2>
                        </div>
                        <div className="card-body text-center">
                            {f !== 0 ? f : null}
                        </div>
                    </div>  )
                    }}
                )}
            </div>
        )
    }
}
