import React, {Component} from 'react';
import Header from "../Header/Header";
import MenuRight from "../MenuRight/MenuRight";
import NoPain from "./emojis/no_pain.png";
import Mild from "./emojis/mild.png";
import Moderate from "./emojis/moderate.png";
import Intense from "./emojis/intence.png";
import Unspeakable from "./emojis/unspeakable.png";
import Style from "./Satisfaction.module.scss";
import Next from './emojis/next.gif';
import {socket} from "../../socket";

class Satisfaction extends Component {

    state = {
        satisfaction: 0
    }

    handleSubmit = () => {
        socket.emit("satisfaction", this.state.satisfaction)
        this.props.onClickEvent();
    }


    render() {
        return (
            <div>
                <Header tour={this.props.tour} pseudo={this.props.pseudo} bassin={this.props.bassin}
                        role={this.props.role} numeroPhase={6}/>
                <div className="row">
                    <div className="col-sm-2">
                        <MenuRight role={this.props.role} pseudo={this.props.pseudo}/>
                    </div>
                    <div className="col-sm-10 text-center">
                        <section className={Style.section}>
                            <header>
                                <h2>Êtes-vous satisfait ?</h2>
                            </header>
                            <div className="row">
                                <div className="col-sm-2">
                                    <img className={Style.cursorOn} src={NoPain} alt={"."}
                                        onClick={() => this.setState({satisfaction: 2})}/>
                                    <br/>
                                    Très Satisfait
                                </div>
                                <div className="col-sm-2">
                                    <img className={Style.cursorOn} src={Mild} alt={"."}
                                         onClick={() => this.setState({satisfaction: 1})}/>
                                    <br/>
                                    Satisfait
                                </div>
                                <div className="col-sm-2">
                                    <img className={Style.cursorOn} src={Moderate} alt={"."}
                                         onClick={() => this.setState({satisfaction: 0})}/>
                                    <br/>
                                    Neutre
                                </div>
                                <div className="col-sm-2">
                                    <img className={Style.cursorOn} src={Intense} alt={"."}
                                         onClick={() => this.setState({satisfaction: -1})}/>
                                    <br/>
                                    Insatisfait
                                </div>
                                <div className="col-sm-2">
                                    <img className={Style.cursorOn} src={Unspeakable} alt={"."}
                                         onClick={() => this.setState({satisfaction: -2})}/>
                                    <br/>
                                    Très Insatisfait
                                </div>
                                <div className="col-sm-2">
                                    <img className={Style.image} src={Next} alt="." onClick={this.handleSubmit}/>
                                </div>
                            </div>
                        </section>
                        <button onClick={this.props.resultats} >Afficher Résultats</button>
                    </div>
                </div>
            </div>
        );
    }
}

export default Satisfaction;
