import React, {Component} from 'react';
import Checkbox from "../utils/Checkbox";
import StyleStrategy from "../Strategy/Strategy.module.scss";
import {socket} from "../../socket";

class FarmerActivity extends Component {
    state = {
        items: ["Grandes Cultures", "Verger", "Bovin Viande", "Viticulture", "Bovin Laitier", "Granivore"],
        selected: new Set(),
        values: [0, 0, 0, 0, 0, 0],
        valueGCultures : 0,
        valueVerger: 0,
        valueBovinV: 0,
        valueBovinL: 0,
        valueViticulture: 0,
        valueGranivore: 0
    }

    getCultures = () => {
        return ([parseInt(this.state.valueGCultures),
                    parseInt(this.state.valueVerger),
                        parseInt(this.state.valueBovinV),
                            parseInt(this.state.valueViticulture),
                                parseInt(this.state.valueBovinL),
                                    parseInt(this.state.valueGranivore)])
    }

    labelToIndex = (label) => {
        return (
            label === "Grandes Cultures" ? 0 :
                label === "Verger" ? 1 :
                    label === "Bovin Viande" ? 2 :
                        label === "Viticulture" ? 3 :
                            label === "Bovin Laitier" ? 4 :
                                label === "Granivore" ? 5 :
                                     -1
        )
    }

    labelToValue = (label) => {
        return (
            label === "Grandes Cultures" ? this.state.valueGCultures :
                label === "Verger" ? this.state.valueVerger :
                    label === "Bovin Viande" ? this.state.valueBovinV :
                        label === "Viticulture" ? this.state.valueViticulture :
                            label === "Bovin Laitier" ? this.state.valueBovinL :
                                label === "Granivore" ? this.state.valueGranivore :
                                    -1
        )
    }

    setValueActivity = (label, value) => {
        if (this.labelToIndex(label) === 0) {this.setState({valueGCultures: value})}
        if (this.labelToIndex(label) === 1) {this.setState({valueVerger: value})}
        if (this.labelToIndex(label) === 2) {this.setState({valueBovinV: value})}
        if (this.labelToIndex(label) === 3) {this.setState({valueViticulture: value})}
        if (this.labelToIndex(label) === 4) {this.setState({valueBovinL: value})}
        if (this.labelToIndex(label) === 5) {this.setState({valueGranivore: value})}
    }

    componentWillMount = () => {
        this.selectedCheckboxes = new Set();
    }

    getValues = () => {
        return this.state.values;
    }

    toggleCheckbox = (label) => {
        const newSelected = new Set();
        if (this.selectedCheckboxes.has(label)) {
            this.setValueActivity(label, 0);
            this.selectedCheckboxes.delete(label);
            this.setState({selected: newSelected});

        } else {
            this.selectedCheckboxes.add(label);
            this.setState({selected: newSelected});
        }
    }

    handleFormSubmit = formSubmitEvent => {
        formSubmitEvent.preventDefault();

        for (const checkbox of this.selectedCheckboxes) {
            console.log(checkbox, 'is selected.');
            //console.log('values are', [this.state.valuePolyculture,
            //                            this.state.valueVerger,
            //                            this.state.valueBauvage,
            //                            this.state.valueMarechage])
        }
        socket.emit("activities", this.props.x,
            this.state.valueGCultures,
            this.state.valueVerger,
            this.state.valueBovinV,
            this.state.valueViticulture,
            this.state.valueBovinL,
            this.state.valueGranivore,
            (response) => {
                console.log(response);})
        console.log("Activities");
    }

    handleChange = (event) => {
        if (this.labelToIndex(event.target.id) === 0) {this.setState({valueGCultures: event.target.value})}
        if (this.labelToIndex(event.target.id) === 1) {this.setState({valueVerger: event.target.value})}
        if (this.labelToIndex(event.target.id) === 2) {this.setState({valueBovinV: event.target.value})}
        if (this.labelToIndex(event.target.id) === 3) {this.setState({valueViticulture: event.target.value})}
        if (this.labelToIndex(event.target.id) === 4) {this.setState({valueBovinL: event.target.value})}
        if (this.labelToIndex(event.target.id) === 5) {this.setState({valueGranivore: event.target.value})}
        /*if (this.labelToIndex(event.target.id) === 0) {this.setState({valuePolyculture: event.target.value})}
        if (this.labelToIndex(event.target.id) === 1) {this.setState({valueVerger: event.target.value})}
        if (this.labelToIndex(event.target.id) === 2) {this.setState({valueBauvage: event.target.value})}
        if (this.labelToIndex(event.target.id) === 3) {this.setState({valueMarechage: event.target.value})}*/
        //this.setValueActivity(event.target.id, event.target.value)
    }

    createCheckbox = (label) => (
        <>
            <label htmlFor={"agri"}>
                <Checkbox label={label} handleCheckboxChange={this.toggleCheckbox} key={label}/>
            </label>
            {(this.selectedCheckboxes.has(label)) ?
                <form onSubmit={this.handleFormSubmit}>
                    <input type="number" id={label} name="tentacles" onChange={this.handleChange} min="1" max="5" value={this.labelToValue(label)}/>
                    <input type="submit" value="Valider"/>
                </form>

                : null}
        </>
    )

    createCheckboxes = () => (
        this.state.items.map((label) =>
            <div className="col-sm-3">
                {this.createCheckbox(label)}
            </div>
        )
    )

    render() {
        return (
            <section>
                <header className="text-center">
                    <div className={StyleStrategy.header2}>
                        Agriculteur : {this.props.x}
                    </div>
                </header>
                <div className={StyleStrategy.header3}>
                    <div className="row text-center">
                        {this.createCheckboxes()}
                        {console.log('Activity of ' + this.props.x + ' are ' + this.getCultures())}
                    </div>
                </div>
                {//console.log(this.state.values)}
                /*<input type="checkbox" name="choix1" value="1">glace vanille</input>
                <label htmlFor="tentacles">Number of tentacles (10-100):</label>
                <input type="number" id="tentacles" name="tentacles"
                       min="10" max="100"/>*/}
            </section>
        )
    }
}

export default FarmerActivity;
