import React, {Component} from "react";
import ReactTimerStopwatch from 'react-stopwatch-timer';
import Image from "./stopwatch.png";
import Style from "./utils.module.scss";


export default class MeetingTime extends Component {
    constructor(props) {
        super(props);
        this.state = {
            displayHours: (props.hours !== 0),
        }
    }

    render() {
        return (
            <div>
                <ReactTimerStopwatch
                        isOn={true}
                        watchType="timer"
                        displayCircle={false}
                        color="white"
                        hintColor="white"
                        displayHours={this.state.displayHours}
                        fromTime={new Date(0, 0, 0, this.props.hours, this.props.minutes, this.props.seconds, 0)}/>
