import { createContext, useState } from 'react';

const TimerContext = createContext({
    timerUpdating: [],
    timerStrategy: [],
    timerActions: [],
    timerEvents: [],
    timerDebriefing: [],
    timerSatisfaction: [],
    timerResults: [],
    updateTimerUpdating: (timer) => { },
    updateTimerStrategy: (timer) => { },
    updateTimerActions: (timer) => { },
    updateTimerEvents: (timer) => { },
    updateTimerDebriefing: (timer) => { },
    updateTimerSatisfaction: (timer) => { },
    updateTimerResults: (timer) => { },
});

export function TimerContextConsumer(props) {

    const [updating, setUpdating] = useState([]);
    const [strategy, setStrategy] = useState([]);
    const [actions, setActions] = useState([]);
    const [events, setEvents] = useState([]);
    const [debriefing, setDebriefing] = useState([]);
    const [satisfaction, setSatisfaction] = useState([]);
    const [results, setResults] = useState([]);

    function updateTimerUpdatingHandler(timer) {
        console.log("HANDLER = ", timer);
        setUpdating(timer);
    }
    function updateTimerStrategyHandler(timer) {
        setStrategy(timer);
    }
    function updateTimerActionsHandler(timer) {
        setActions(timer);
    }
    function updateTimerEventsHandler(timer) {
        setEvents(timer);
    }
    function updateTimerDebriefingHandler(timer) {
        setDebriefing(timer);
    }
    function updateTimerSatisfactionHandler(timer) {
        setSatisfaction(timer);
    }
    function updateTimerResultsHandler(timer) {
        setResults(timer);
    }

    const context = {
        timerUpdating: updating,
        timerStrategy: strategy,
        timerActions: actions,
        timerEvents: events,
        timerDebriefing: debriefing,
        timerSatisfaction: satisfaction,
        timerResults: results,
        updateTimerUpdating: updateTimerUpdatingHandler,
        updateTimerStrategy: updateTimerStrategyHandler,
        updateTimerActions: updateTimerActionsHandler,
        updateTimerEvents: updateTimerEventsHandler,
        updateTimerDebriefing: updateTimerDebriefingHandler,
        updateTimerSatisfaction: updateTimerSatisfactionHandler,
        updateTimerResults: updateTimerResultsHandler
    };

    return <TimerContext.Consumer value={context}>
        {props.children}
    </TimerContext.Consumer>
}

export default TimerContext;
