import React, {Component} from "react";
import MeetingTime from "../utils/MeetingTime";
import CallingList from "./CallingList";
import Style from "../Updating/Updating.module.scss";
import StyleStrategy from "./Strategy.module.scss";
import ImageArgent from "../Updating/argent.png";
import ImageTemps from "../Updating/montre_circulaire.png";
import ImageRDV from "./priseRDV.png";
import Sablier from "./sablier.png";
import StopWatch from "./cadre.png";
import Header from "../Header/Header";
import formationId from "../utils/formationId";
import MenuRight from "../MenuRight/MenuRight";
import StyleButton from "../utils/button.module.scss";

export default class Strategy extends Component {
    constructor(props) {
        super(props);
        this.state = {
            ancient: (props.role === 0 ? "le gestionnaire" : "l'élu"),
            child: (props.role === 0) ? "agriculteurs" : "élus"
            /*style: (props.role !== 1) ? "col-sm-6" : "col-sm-"*/
        }
    }

    callingPlayers() {
        return (
            <section>
                <header className={StyleStrategy.header2}>
                    Les {this.state.child} vous appelant
                </header>
                <ul>
                    {this.props.listPlayers.map((f, index) =>
                        (<li className={StyleStrategy.callingPlayer}> <b>{f}</b> </li>)
                    )}
                </ul>
            </section>
        )
    }

    renderRDV() {
        /* ELU */
        if(this.props.role === 0) {
            return (
                <div className="row text-center">
                    <div className="col-sm-5 border-right border-dark">
                        <div className={StyleStrategy.prendreRDV}>
                            <ul>
                                <li><img onClick={()=>document.write("RDV")} src={ImageRDV} className={StyleStrategy.RDV} alt={"/"}/></li>
                                <li className={StyleStrategy.texte2}>Prendre un rendez-vous avec le gestionnaire du bassin</li>
                            </ul>
                        </div>
                    </div>
                    <div className="col-sm-7">
                        {this.callingPlayers()}
                    </div>
                </div>
            )
        } /* GESTIONNAIRE */
        else if(this.props.role === 2) {
            return (<div className="text-center">
                {this.callingPlayers()}
            </div>);
        } /* AGRI */
        else if(this.props.role === 1) {
            return (<div className="text-center">
                <div className={StyleStrategy.prendreRDV}>
                    <ul>
                        <li><img onClick={()=>document.write("RDV")} src={ImageRDV} className={StyleStrategy.RDV2} alt={"/"}/></li>
                        <li className={StyleStrategy.texte2}>Prendre un rendez-vous avec l'élu de votre bassin</li>
                    </ul>
                </div>
            </div>)
        }
        else {return null;}
    }

    render() {
        return (
            <>
                <Header tour={this.props.tour} pseudo={this.props.pseudo} bassin={this.props.bassin} role={this.props.role} numeroPhase={2}  h={0} m={2} s={30} />
                <div className="row">
                    <div className="col-sm-2">
                        <MenuRight ut={this.props.ut} ub={this.props.ub} formations={this.props.formations} role={this.props.role}/>
                        {/*<div className={StyleStrategy.containerPrime}>
                            <ul className="text-center">
                                <p className={StyleStrategy.texte}>Mes ressources :</p>
                                <li>U T : {this.props.ut }</li>
                                <li>U B : {this.props.ub }</li>
                                {(this.props.role === 3) ? <div> <p className={StyleStrategy.texte}>Mes compétences :</p>{this.props.formations.map((f, index) =>
                                    {if (f !== 0){
                                        return (<li> {formationId(index)} {f} </li>)
                                    }}
                                )} </div>: null}
                            </ul>
                        </div>*/}
                    </div>
                    <div className="col-sm-10">
                        <section className={StyleStrategy.none}>
                            <header className="text-center">
                                <div className={StyleStrategy.header}>
                                    Service de prise de rendez-vous
                                </div>
                            </header>
                            {this.renderRDV()}
                        </section>
                        <button onClick={this.props.onClickEvent} className={"btn btn-small btn-primary"}>Next</button>
                    </div>
                </div>
            </>

        )
    }
}
