import React, {Component} from 'react';
import Style from "./Pratiques.module.scss";
import GrandesCultures from "./Cultures/grandes_cultures.png";
import Verger from "./Cultures/verger.webp";
import BovinViande from "./Cultures/bovinViande.png";
import BovinLaitier from "./Cultures/bovinLaitier.png";
import Viticulture from "./Cultures/viticulture.webp";
import Granivore from "./Cultures/granivore.png";

class Pratiques extends Component {

    cardPratiques(image, titre, sousTitre, cost, prod, pollution, classNameImage, classNameSection) {
        return (
            <>
                <section className={classNameSection}>

                            <div className="row">
                                <div className="col-sm-4">
                                    <img src={image} alt={"/"} className={classNameImage}/>
                                </div>
                                <div className="col-sm-8">
                                    <div className="text-center">
                                        <p className={Style.Title}>{titre}</p>
                                        <p className={Style.SubTitle}>{sousTitre}</p>
                                        <p>Coût : <b>{cost[0]}</b> UT & <b>{cost[1]}</b> UB</p>
                                        <p>Production : {prod}</p>
                                        <p>Pollution : {pollution}</p>
                                    </div>

                                </div>
                            </div>
                </section>
            </>
        )
    }

    render() {
        return (
            <>
                <div className="row">
                    <div className="col-sm-4">
                        {this.cardPratiques(BovinViande, "Bovin Viande", "Hors-Sol", [3,4], 5, 3, Style.BovinViande, Style.BovinViandeCard)}
                    </div>
                    <div className="col-sm-4">
                        {this.cardPratiques(BovinViande, "Bovin Viande", "50% Pâturage", [3,2], 3, 2, Style.BovinViande, Style.BovinViandeCard)}
                    </div>
                    <div className="col-sm-4">
                        {this.cardPratiques(BovinViande, "Bovin Viande", "100% Pâturage", [3,1], 2, 0, Style.BovinViande, Style.BovinViandeCard)}
                    </div>

                </div>

                <div className="row">
                    <div className="col-sm-4">
                        {this.cardPratiques(GrandesCultures, "Grandes Cultures", "Conventionnel", [3,4], 5, 3, Style.BovinViande, Style.GrandesCulturesCard)}
                    </div>
                    <div className="col-sm-4">
                        {this.cardPratiques(GrandesCultures, "Grandes Cultures", "Conventionnel Irrigué", [3,4], 5, 3, Style.BovinViande, Style.GrandesCulturesCard)}
                    </div>
                    <div className="col-sm-4">
                        {this.cardPratiques(Verger, "Verger", "Conventionnel", [3,3], 5, 3, Style.BovinViande, Style.VergerCard)}
                    </div>
                </div>
                <div className="row">
                    <div className="col-sm-4">
                        {this.cardPratiques(Verger, "Verger", "Conventionnel Irrigué", [3,3], 5, 3, Style.BovinViande, Style.VergerCard)}
                    </div>
                    <div className="col-sm-4">
                        {this.cardPratiques(Verger, "Verger", "Raisonné", [4,2], 4, 2, Style.BovinViande, Style.VergerCard)}
                    </div>
                    <div className="col-sm-4">
                        {this.cardPratiques(Verger, "Verger", "Raisonné Irrigué", [4,2], 4, 2,Style.BovinViande, Style.VergerCard)}
                    </div>
                </div>
                <div className="row">
                    <div className="col-sm-4">
                        {this.cardPratiques(Verger, "Verger", "Bio", [5,1], 3, 1, Style.BovinViande, Style.VergerCard)}
                    </div>
                    <div className="col-sm-4">
                        {this.cardPratiques(Verger, "Verger", "Bio Irrigué", [5,1], 3, 1, Style.BovinViande, Style.VergerCard)}
                    </div>
                    <div className="col-sm-4">
                        {this.cardPratiques(Viticulture, "Viticulture", "Conventionnel", [3,3], 5, 3, Style.BovinViande, Style.ViticultureCard)}
                    </div>
                </div>
                <div className="row">
                    <div className="col-sm-4">
                        {this.cardPratiques(Viticulture, "Viticulture", "Conventionnel Irrigué", [3,3], 5, 3, Style.BovinViande, Style.ViticultureCard)}
                    </div>
                    <div className="col-sm-4">
                        {this.cardPratiques(Viticulture, "Viticulture", "Raisonné", [4,2], 4, 2, Style.BovinViande, Style.ViticultureCard)}
                    </div>
                    <div className="col-sm-4">
                        {this.cardPratiques(Viticulture, "Viticulture", "Raisonné Irrigué", [4,2], 4, 2, Style.BovinViande, Style.ViticultureCard)}
                    </div>

                </div>
                <div className="row">
                    <div className="col-sm-4">
                        {this.cardPratiques(Viticulture, "Viticulture", "Bio", [5,1], 3, 1, Style.BovinViande, Style.ViticultureCard)}
                    </div>
                    <div className="col-sm-4">
                        {this.cardPratiques(Viticulture, "Viticulture", "Bio Irrigué", [5,1], 3, 1, Style.BovinViande, Style.ViticultureCard)}
                    </div>
                    <div className="col-sm-4">
                        {this.cardPratiques(BovinLaitier, "Bovin Laitier", "", [5,1], 3, 1, Style.BovinViande, Style.BovinLaitierCard)}
                    </div>
                </div>
                <div className="row">

                    <div className="col-sm-4">
                        {this.cardPratiques(Granivore, "Granivore", "Plein Air", [4,2], 4, 1, Style.BovinViande, Style.GranivoreCard)}
                    </div>
                    <div className="col-sm-4">
                        {this.cardPratiques(Granivore, "Granivore", "Intensif", [5,3], 5, 3, Style.BovinViande, Style.GranivoreCard)}
                    </div>
                </div>
            </>

        );
    }
}

export default Pratiques;
