import React, {Component} from 'react';
import Style from "../Header/Header.module.scss";
import StyleStrategy from "../Strategy/Strategy.module.scss";
import {socket} from "../../socket";

class SetTimeInterface extends Component {

    state = {
        updating: [0, 0, 0],
        strategy: [0, 0, 0],
        actions: [0, 0, 0],
        events: [0, 0, 0],
        debriefing: [0, 0, 0],
        satisfaction: [0, 0, 0],
        results: [0, 0, 0]
    }

    indexToInterface = (index) => {
        return (
            (index === "0") ? this.state.updating :
                (index === "1") ? this.state.strategy :
                    (index === "2") ? this.state.actions :
                        (index === "3") ? this.state.events :
                            (index === "4") ? this.state.debriefing :
                            (index === "5") ? this.state.satisfaction :
                            (index === "6") ? this.state.results :
                                null
        )
    }

    updateTime = (index, newValues) => {
        if(index === "0") this.setState({updating: newValues})
        if(index === "1") this.setState({strategy: newValues})
        if(index === "2") this.setState({actions: newValues})
        if(index === "3") this.setState({events: newValues})
        if(index === "4") this.setState({debriefing: newValues})
        if(index === "5") this.setState({satisfaction: newValues})
        if(index === "6") this.setState({results: newValues})
    }

    handleFormSubmit = formSubmitEvent => {
        formSubmitEvent.preventDefault();
        console.log(
            'Updating : ' + this.state.updating +'\n'+
            'Strategy : ' + this.state.strategy +'\n'+
            'Actions : ' + this.state.actions +'\n'+
            'Events : ' + this.state.events +'\n'+
            'Debriefing : ' + this.state.debriefing
        );
    }

    handleSubmit = () => {
        socket.emit("timers", this.state.updating, this.state.strategy,
                this.state.actions, this.state.events, this.state.debriefing, this.state.satisfaction,
            this.state.results, (response) => {
                    console.log(response);})
        console.log("Timers");
    }
        //socket.emit("timers", , (response)=>{
        //    console.log("Setting")
        //    })


    handleChange = (event) => {
        const value = (event.target.value === "" ) ? 0 : parseInt(event.target.value);
        if (event.target.id[1] === "0") {this.updateTime(event.target.id[0], [(value < 24) ? value : 24, this.indexToInterface(event.target.id[0])[1], this.indexToInterface(event.target.id[0])[2]])}
        if (event.target.id[1] === "1") {this.updateTime(event.target.id[0], [this.indexToInterface(event.target.id[0])[0], (value < 60) ? value : 60, this.indexToInterface(event.target.id[0])[2]])}
        if (event.target.id[1] === "2") {this.updateTime(event.target.id[0], [this.indexToInterface(event.target.id[0])[0], this.indexToInterface(event.target.id[0])[1], (value < 60) ? value : 60])}
    }

    onClickEvent = () => {
        this.handleSubmit();
        this.props.nextInterface();
    }

    createForm = (index, name) => {
        return (
            <div className={StyleStrategy.textActions}>
                <div className="text-center">
                    {name} :
                    <form onSubmit={this.handleFormSubmit}>
                        <div className={"text-center"}>
                            <label className={StyleStrategy.header3} htmlFor={index+"0"}>hours : </label>
                            <input className={StyleStrategy.textArea} type="text" id={index+"0"} name="tentacles" onChange={this.handleChange} value={this.indexToInterface(index)[0]}/>
                        </div>
                        <div className={"text-center"}>
                            <label className={StyleStrategy.header3} htmlFor={index+"1"}>minutes : </label>
                            <input className={StyleStrategy.textArea} type="text" id={index+"1"} name="tentacles" onChange={this.handleChange} value={this.indexToInterface(index)[1]}/>
                        </div>
                        <div className={"text-center"}>
                            <label className={StyleStrategy.header3} htmlFor={index+"2"}>secondes : </label>
                            <input className={StyleStrategy.textArea} type="text" id={index+"2"} name="tentacles" onChange={this.handleChange} value={this.indexToInterface(index)[2]}/>
                        </div>
                        <div className={"text-center"}>
                            <input className={StyleStrategy.valideArea} type="submit" value="Valider"/>
                        </div>
                    </form>
                </div>
            </div>
        )
    }

    render() {
        /*let updating = [0, 0, 0];
        let strategy = [0, 0, 0];
        let actions = [0, 0, 0];
        let debriefing = [0, 0, 0];
        let events = [0, 0, 0];*/
        return (
            <>
                <div className={Style.Header}>
                    <div className={Style.Content}>
                        <div className="text-center">
                            <div className={Style.TextColor}>
                                Jeu Sérieux - Le Bassin de Moderbach
                            </div>
                        </div>
                        <div className={Style.TextColor}>
                            <div className="row text-center">
                                <div className="col-sm-6">Pseudo : {this.props.pseudo}</div>
                                <div className="col-sm-6">Rôle : Animateur</div>
                            </div>
                        </div>
                    </div>
                </div>
                <div>
                    <div className="text-center">
                        <button className={StyleStrategy.nextInterface} onClick={this.onClickEvent}>
                            Passer à l'interface suivante
                        </button>
                    </div>
                    <section className={StyleStrategy.none4}>
                        <header className="text-center">
                            <div className={StyleStrategy.header}>
                                Choisir le temps pour chaque phase du tour
                            </div>
                        </header>
                        <section className="row">
                            <div className="col-sm-4">
                                {this.createForm("0", "Mise à jour des ressources")}
                            </div>
                            <div className="col-sm-4">
                                {this.createForm("1", "Élaboration de la stratégie")}
                            </div>
                            <div className="col-sm-4">
                                {this.createForm("2", "Choix des actions")}
                            </div>
                        </section>
                        <br/>
                        <section className="row">
                            <div className="col-sm-4">
                                {this.createForm("3", "Découverte des événements")}
                            </div>
                            <div className="col-sm-4">
                                {this.createForm("4", "Réunion collective")}
                            </div>
                            <div className="col-sm-4">
                                {this.createForm("5", "Satisfaction")}
                            </div>
                        </section>
                        <section>
                            <div>
                                {this.createForm("6", "Résultats")}
                            </div>
                        </section>

                    </section>
                </div>
            </>
        );
    }
}

export default SetTimeInterface;
