import React, {Component} from "react";
import Header from "../Header/Header";
import MenuRight from "../MenuRight/MenuRight";
import StyleStrategy from "../Strategy/Strategy.module.scss";
import Invitation from "./envelope-number.png"
import {socket} from "../../socket";
import calculateTime from "../utils/calculateTime";

export default class Debriefing extends Component {
    constructor(props) {
        super(props);
        this.state = {
            timer: []
        }
    }

    componentDidMount() {
        socket.emit("getTimer", 5,(response) => {
            console.log(response);
            console.log(calculateTime(response));
            this.setState({timer: calculateTime(response)});
            console.log("State : ", this.state.timer);
        })
    }

    invitePlayers() {
        if(this.props.role === 2) {
            return (
                <div className="text-center">
                    <ul className={StyleStrategy.maladie}>
                        <li><img onClick={null} src={Invitation} className={StyleStrategy.RDV2} alt={"/"}/></li>
                        <li className={StyleStrategy.texteInvitation}>Inviter les joueurs du bassin versant à une réunion collective</li>
                    </ul>
                </div>
            )
        } else {
            return (
                <div className="text-center">
                    <p className={StyleStrategy.texteInvitation}><i>Le gestionnaire du Bassin vous invite à une réunion collective</i></p>
                </div>
            )
        }
    }

    render() {
        return (
            <>
                <Header tour={this.props.tour} pseudo={this.props.pseudo} bassin={this.props.bassin} role={this.props.role} numeroPhase={5}  h={this.state.timer[0]} m={this.state.timer[1]} s={this.state.timer[2]}
                    finishEvent={this.props.onClickEvent}/>
                <div className="row">
                    <div className="col-sm-2">
                        <MenuRight ut={this.props.ut} ub={this.props.ub} formations={this.props.formations} role={this.props.role} pseudo={this.props.pseudo}/>
                        {/*<div className={StyleStrategy.containerPrime}>
                            <ul className="text-center">
                                <p className={StyleStrategy.texte}>Mes ressources :</p>
                                <li>U T : {this.props.ut }</li>
                                <li>U B : {this.props.ub }</li>
                                {(this.props.role === 3) ? <div> <p className={StyleStrategy.texte}>Mes compétences :</p>{this.props.formations.map((f, index) =>
                                    {if (f !== 0){
                                        return (<li> {formationId(index)} {f} </li>)
                                    }}
                                )} </div>: null}
                            </ul>
                        </div>*/}
                    </div>
                    <div className="col-sm-10">
                        <section className={StyleStrategy.none}>
                            <header className="text-center">
                                <div className={StyleStrategy.header}>
                                    Réunion collective de fin du tour
                                </div>

                            </header>
                            {this.invitePlayers()}
                        </section>
                        <button onClick={this.props.onClickEvent} className={"btn btn-small btn-primary"}>Next</button>
                    </div>
                </div>
            </>
        );
    }

}
