import React, {Component} from "react";
import meteoToString from "../utils/meteoToString";
import Header from "../Header/Header";
import MenuRight from "../MenuRight/MenuRight";
import StyleStrategy from "../Strategy/Strategy.module.scss";
import Soleil from "./soleil.png";
import Pluie from "./pluie.png";
import Maladie from "./maladie.png";
import Eau from "./eau.png";
import Territoire from "./territoire.png";
import {socket} from "../../socket";
import calculateTime from "../utils/calculateTime";

export default class Events extends Component {
    constructor(props) {
        super(props);
        this.state = {
            timer: []
        }
    }

    componentDidMount() {
        socket.emit("getTimerEvents", (response) => {
            console.log(response);
            console.log(calculateTime(response));
            this.setState({timer: calculateTime(response)});
            console.log("State : ", this.state.timer);
        })
    }

    meteoAnnee(annee, index) {
        return (
            <div className="col-sm-4 text-center">
                <div className={StyleStrategy.meteo}>
                    <header className={StyleStrategy.header3}>
                        Année {index}
                    </header>
                    <p className={StyleStrategy.texteMeteo}>
                        <img src={Pluie} className={StyleStrategy.ImageMeteo}/> Hiver {meteoToString(annee[0])}
                    </p>
                    <p className={StyleStrategy.texteMeteo}>
                        <img src={Soleil} className={StyleStrategy.ImageMeteo}/> Été {meteoToString(annee[1])}
                    </p>
                </div>
            </div>
        );
    }

    indicateursBassin() {
        if(this.props.role === 2) {
            return (
                <>
                    <header className="text-center">
                        <div className={StyleStrategy.header}>
                            Qualité de l'eau du Bassin
                        </div>
                    </header>
                    <div className="text-center">
                        <div className={StyleStrategy.maladie}>
                            <p className={StyleStrategy.texteMeteo}>
                                <img src={Eau} className={StyleStrategy.ImageMaladie}/>
                                <h2>{this.props.indicateurs[0]}</h2>
                            </p>
                        </div>
                    </div>
                </>)
        }
        if(this.props.role === 0) {
            return (
                <>
                    <header className="text-center">
                        <div className={StyleStrategy.header}>
                            Qualité du territoire
                        </div>
                    </header>
                    <div className="text-center">
                        <div className={StyleStrategy.maladie}>
                            <p className={StyleStrategy.texteMeteo}>
                                <img src={Territoire} className={StyleStrategy.ImageMaladie}/>
                                <h2>{this.props.indicateurs[1]}</h2>
                            </p>
                        </div>
                    </div>
                </>)
        }
    }


    render() {
        return (
            <>
                <Header tour={this.props.tour} pseudo={this.props.pseudo} bassin={this.props.bassin} role={this.props.role} numeroPhase={4}   h={this.state.timer[0]} m={this.state.timer[1]} s={this.state.timer[2]}
                        finishEvent={this.props.onClickEvent}/>
                <div className="row">
                    <div className="col-sm-2">
                        <MenuRight ut={this.props.ut} ub={this.props.ub} formations={this.props.formations} role={this.props.role} pseudo={this.props.pseudo}/>
                        {/*<div className={StyleStrategy.containerPrime}>
                            <ul className="text-center">
                                <p className={StyleStrategy.texte}>Mes ressources :</p>
                                <li>U T : {this.props.ut }</li>
                                <li>U B : {this.props.ub }</li>
                                {(this.props.role === 3) ? <div> <p className={StyleStrategy.texte}>Mes compétences :</p>{this.props.formations.map((f, index) =>
                                    {if (f !== 0){
                                        return (<li> {formationId(index)} {f} </li>)
                                    }}
                                )} </div>: null}
                            </ul>
                        </div>*/}
                    </div>
                    <div className="col-sm-10">
                        <section className={StyleStrategy.none2}>
                            <header className="text-center">
                                <div className={StyleStrategy.header}>
                                    Météo des 3 années passées
                                </div>
                            </header>
                            <div className="row">
                                {this.props.meteorology.map((annee, index) =>
                                    this.meteoAnnee(annee, index))}
                            </div>
                            <header className="text-center">
                                <div className={StyleStrategy.header}>
                                    Événements particuliers
                                </div>
                            </header>
                            <div className="text-center">
                                <div className={StyleStrategy.maladie}>
                                    <header className={StyleStrategy.header3}>
                                        Maladie & Bio-agresseurs
                                    </header>
                                    <p className={StyleStrategy.texteMeteo}>
                                        <img src={Maladie} className={StyleStrategy.ImageMaladie}/>
                                        <i> {(this.props.maladie === false) ? "Pas d'" : ""}Attaque de bio-agresseurs</i>
                                    </p>
                                </div>
                            </div>
                            {this.indicateursBassin()}
                        </section>
                        <button onClick={this.props.onClickEvent} className={"btn btn-small btn-primary"}>Next</button>
                    </div>
                </div>
            </>)

                    {/*<div className="row">
                <div className="col-sm-4">
                </div>
                <div data-autoplay={12000}/>
                <div className="col-sm-4">

                </div>
                <div className="col-sm-4">

                </div>

                {this.props.meteorology.map((annee, index) =>
                    annee.map((saison, i) =>
                        <div className="col-sm-4">
                            Année {index} : {i === 0 ?
                            <h2>Hiver {meteoToString(saison)}</h2> :
                            <h2>Été {meteoToString(saison)}</h2>}
                        </div>))}
                {this.props.role === 1 ? <h3>Indice de qualité du territoire : {this.props.iqTerritoire}</h3> :
                    this.props.role === 2 ? <h3>Indice de qualité de l'eau du bassin : {this.props.iqBassin}</h3>:
                        null
                }
            </div>*/}

    }
}
