export default function calculateTime(secondsInitial) {
    const hours = Math.floor(secondsInitial / 3600);
    const minutes = Math.floor((secondsInitial - hours * 3600) / 60);
    const seconds = secondsInitial - hours * 3600 - minutes * 60;
    return [hours, minutes, seconds];
}
