/* 
    Function : handleClickTile

    Syntax  : handleClickTile(h)

    Input   : h : data of the selectedTile

    Description : 
        change the state of selectedTile based on which tile is clicked and the current state
        if the current state's id is equal to h's id, the state is set to null
        else it is updated to h's data     

    Author : Hugo KELHETTER
*/
import {socket} from "../../socket";
import isIncluded from "../../Interface/utils/isIncluded";
import deleteArray from "../../Interface/utils/deleteArray";

function handleClickTileFarmer(h) {
    console.log("SELECTED IFarmerD : ", this.state.selectedTile)
    console.log("IS INCLUDED : ", isIncluded(h.props.id, this.state.selectedTile))
    if(isIncluded(h.props.id, this.state.selectedTile)[0]) {
        this.setState({selectedTile: deleteArray(h.props, this.state.selectedTile)})
    }
    /*if (h.props.id === this.state.selectedTile?.id) {
        this.setState({ selectedTile: null })
    }*/
    else {
        this.setState({ selectedTile: this.state.selectedTile.push(h.props) })
        console.log("STATE ID : ", this.state.id);
        //console.log("SELECTED TILE ", this.state.selectedTile?.id);
        if(this.props.role === 1){
            socket.emit("getPlayerActivity", this.state.id, h.props.id, (response => {
                console.log("GET PLAYER ACTIVITY IS : ", response[0]);
                //let { selectedTile } = this.state;
                this.setState({
                    selectedTile : {
                        ...this.state.selectedTile,
                        practice: response[0].practice
                    }
                })
                //console.log(this.state.selectedTile.practice);
                /*const newSelectedTile = this.state.selectedTile;
                //console.log(newSelectedTile);
                //newSelectedTile.practice = response[0].practice;
                this.setState({[this.state.selectedTile.practice]: response[0].practice});
                console.log(this.state.selectedTile)*/
            }))
        }
    }
}
export default handleClickTileFarmer
