import React, {Component} from "react";
import StyleStrategy from "../Strategy/Strategy.module.scss";
import formationId from "../utils/formationId";
import {socket} from "../../socket";

export default class MenuRight extends Component {
    state = {
        competences: [],
        resources: []
    }

    componentDidMount() {
        socket.emit("getStats", this.props.pseudo, (response) => {
            console.log(response[0]);
            const resources = [response[0].ut, response[0].ub]
            const competences = [response[0].Polyculture, response[0].Verger,
                response[0].Bauvage, response[0].Marechage];
            this.setState({competences, resources})
            //console.log(calculateTime(response));
            //this.setState({timer: calculateTi, me(response)});
            //console.log("State : ", this.state.timer);
        })
    }

    render() {
        return (
            <div className={StyleStrategy.containerPrime}>
                <ul>
                    <p className={StyleStrategy.texte}>Mes ressources :</p>
                    <li>U B : {this.state.resources[1] }</li>
                    <li>U T : {this.state.resources[0] }</li>
                    {(this.props.role === 1) ? <div> <p className={StyleStrategy.texte}>Mes comp√©tences :</p>{this.state.competences.map((f, index) =>
                        {if (f !== 0){
                            return (<li> {formationId(index)} {f} </li>)
                        }}
                    )} </div>: null}
                    <button onClick={this.props.onClickBank} className={StyleStrategy.banque}>Banque</button>
                </ul>
            </div>
        )
    }
}
