import React, {Component} from "react";
import Style from "./Updating.module.scss";
import ImageUp from "./up.png";
import ImageDown from "./down.png";
import ImageArgent from "./argent.png";
import ImageTemps from "./montre_circulaire.png";
import Header from "../Header/Header";
import formationId from "../utils/formationId";
import MenuRight from "../MenuRight/MenuRight";
import {socket} from "../../socket";
import calculateTime from "../utils/calculateTime";


export default class Updating extends Component {
    constructor(props) {
        super(props);
        this.state = {
            colonnes: ["col-sm-12", "col-sm-6", "col-sm-4", "col-sm-3"],
            imageUt: (this.props.oldUt >= 0) ? ImageUp : ImageDown,
            imageUb: (this.props.oldUb >= 0) ? ImageUp : ImageDown,
            timer: [],
            isOn: false
        }
    }

    /*setTimer = () => {
        socket.emit("getTimerUpdating", (response) => {
            console.log(response);
            console.log(calculateTime(response));
            this.setState({timer: calculateTime(response)});
            console.log("State : ", this.state.timer);
        })
    }*/

    componentDidMount() {
        socket.emit("getTimerUpdating", (response) => {
            console.log(response);
            console.log(calculateTime(response));
            this.setState({timer: calculateTime(response)});
            console.log("State : ", this.state.timer);
        })
    }

    nombreElements() {
        var indice = 0;
        for(var i = 0; i < 4; i++){
            if(this.props.formations[i] !== 0) {
                indice += 1;
            }
        }
        return indice;
    }



    render() {
        return (
            <>
                <Header tour={this.props.tour} isOn={this.state.isOn} pseudo={this.props.pseudo} bassin={this.props.bassin} role={this.props.role} numeroPhase={1} h={parseInt(this.state.timer[0])} m={parseInt(this.state.timer[1])} s={parseInt(this.state.timer[2])}
                    finishEvent={this.props.onClickEvent}/>
                {setTimeout( () => {this.setState({isOn: true})}, 500)}
                <div className="row">
                    <div className="col-sm-2">
                        <MenuRight ut={this.props.ut} ub={this.props.ub} formations={this.props.formations} role={this.props.role}/>
                    </div>
                    <div className="col-sm-10">
                        <section className={Style.Updating}>
                            Vos ressources au début du tour :
                            <div className="row text-center">
                                <div className="col-sm-6">
                                    <h2 className={Style.TextColor}>
                                        <img className={Style.Image} src={ImageArgent}/> UB : {this.props.ub} <img className={Style.Image} src={this.state.imageUb}/> {this.props.oldUb}
                                    </h2>
                                </div>
                                <div className="col-sm-6">
                                    <h2 className={Style.TextColor}>
                                        <img className={Style.Image} src={ImageTemps}/> UT : {this.props.ut} <img className={Style.Image} src={this.state.imageUt}/> {this.props.oldUt}
                                    </h2>
                                </div>
                            </div>
                            {this.props.role === 1 ? (<div>Vos niveaux de compétence :
                                <div className={"row text-center"}>{this.props.formations.map((f, index) =>
                                    {if (f !== 0){
                                        return (<div className={this.state.colonnes[this.nombreElements(this.props.formations)-1]}>
                                            <p className={Style.TextColor}> {formationId(index)} </p>
                                            <p className={Style.TextDecor}> {f} </p>
                                        </div>  )
                                    }}
                                )}</div></div>) : null}
                        </section>
                        <button onClick={this.props.onClickEvent} className={"btn btn-small btn-primary"}>Next</button>
                    </div>
                </div>
            </>
        )
    }
}
