import React, {useContext} from 'react'
import {generateHexes, generateRivers, getSubBassin} from "./map/MapUtil.js"
import Bassin from "./map/Bassin.js"
import ActivitySwapper from "./controls/ActivitySwapper.js"
import ValidationTour from "./controls/ValidationTour.js"
import handleClickTile from './controls/handleClickTileFarmer.js'
import Chat from "./Chat.js"
import { socket } from "../socket.js"
import Ressources from "./controls/Ressources.js"
import '../index.css'
import PropTypes from 'prop-types';
import SlideField from "./controls/SlideField.js"
import Menu from "./controls/Menu.js"
import Diary from "./Diary.js"
import roleToString from '../Interface/utils/roleToString'
import { Button } from '@material-ui/core';
import Updating from "../Interface/Updating/Updating";
import {nameSubBassin} from "./map/MapUtil.js";
import MenuContext from "../Menu/menu-context";
import InterfaceContext from "./interface-context";
import Strategy from "../Interface/Strategy/Strategy";
import Events from "../Interface/Events/Events";
import Debriefing from "../Interface/Debriefing/Debriefing";
import Style from "../Interface/Strategy/Strategy.module.scss";
import Pratiques from "../Interface/ActionChoice/Pratiques";
import DetailsParcelle from "../Interface/ActionChoice/DetailsParcelle";
import Header from "../Interface/Header/Header";
import MenuRight from "../Interface/MenuRight/MenuRight";
import StylePratique from "../Interface/ActionChoice/Pratiques.module.scss";
import Results from "../Interface/Results/Results";
import Satisfaction from "../Interface/Satisfaction/Satisfaction";
import calculateTime from "../Interface/utils/calculateTime";
import TimerContext from "./animator/timers-context";


class Conteneur extends React.Component {
    /* 
        Input : props={name,role}
                name : string : player's name
                role : number : player's name

        Syntax : <Game name={name} role={role} />
    */

    static contextType = TimerContext

    constructor(props) {
        super(props)
        //this.changeTileActivity = this.changeTileActivity.bind(this)
        this.handleClickTile = handleClickTile.bind(this)
        //this.a = this.a.bind(this)


        this.state = {
            map: {moreHexas: "", moreRivers: null},
            selectedTile: null, HexasTampon: null,
            cost: {},
            tour: 0,
            fini: false,
            actions: {},
            timer: [],
            allCostsUt : [],
            allCostsUb : [],
            lstConvo: {},
            displayDiary: false,
            disconnected: false,
            displaySlider: false,
            inputPhase: true,
            interfaceCtx: 1,
            selectedTilePractise: 0,
            competences: [],
            resources: [],
            totalUt: 0,
            totalUb: 0
        }
    }

    static propTypes = {
        name: PropTypes.string.isRequired,
        role: PropTypes.number.isRequired
    }

    //timers = useContext(TimerContext);
    /*
        Function : createTampon

        Syntax  : HexasTampon=createTampon(moreHexas)

        Input   : moreHexas : object containing data to create hexagons

        Outputs : HexasTampon : a copy of moreHexas containing a subset of entries

        Description : 
            create a copy of moreHexas containing a subset of entries of moreHexas.
            the remaining entries are those the players can change directly.

        Author : Hugo KELHETTER
    --------------------------------------------------------------------------------------------------------
    not in current use
    --------------------------------------------------------------------------------------------------------
    */
    createTampon(moreHexas, player) {

        let HexasTampon = {}
        for (const key in moreHexas) {
            if (moreHexas[key].player === player) {
                let hex = {};
                hex.activity = moreHexas[key].activity
                hex.player = moreHexas[key].player
                HexasTampon[key] = hex
            }
        }
        return HexasTampon
    }

    /* 
        Function : updateMap

        Syntax  : updateMap(newData)

        Input   : object containing the data to update the state

        Description : update the state of the map with new data received from the server. call createTampon automatically

        Author : Hugo KELHETTER
    */
    updateMap(newData) {
        const newHexas = {}
        for (const index in newData) {
            let newValues = this.state.map.moreHexas
            for (const key in newData[index]) {
                newValues[key] = newData[index][key]
            }
            newHexas[index] = newValues
        }
        this.setState({moreHexas: newHexas, cost: {}})
        //this.createTampon(this.state.map.moreHexas, this.state.map.player)
    }

    /*
        Function : changeTileActivity

        Syntax
            changeTileActivity(value,changeAll)

        Input
            value       :new value for the selectedTile's activity
            changeAll   :false:only change the selectedTile's activity
                        :true :change all tiles from the player

        Description
            updates the state in 2 ways:
                changes the selectedTile or all tiles depending on the value of changeAll
                set the state value of selectedTile to null  

        Author : Hugo KELHETTER
    */
    changeTileActivity = (value, changeAll) => {
        const hexagons = this.state.map.moreHexas;

        if (changeAll) {
            const newAction = {}
            const newCost = {}
            const player = this.state.id//const player = hexagons[this.state.selectedTile.id].player
            Object.values(hexagons).forEach(hex => {
                if (hex.player === player) {
                    hex.modified = true
                    newAction[hex.Id] = value.Id
                    newCost[hex.Id] = {ub: value.Intrants, ut: value.Travail}
                }
            })
            this.setState({actions: newAction, cost: newCost})
        } else {
            let cost = this.state.cost
            cost[this.state.selectedTile.id] = {ub: value.Intrants, ut: value.Travail}
            this.setState({actions: {...this.state.actions, [this.state.selectedTile.id]: value.Id, cost}})
            hexagons[this.state.selectedTile.id - 1].modified = true
        }
        this.setState({map: {...this.state.map, moreHexas: hexagons}, selectedTile: null})

        /*
                 fetch("https://formsubmit.co/ajax/b6d145cfd9512d53d10dd9f9a938ae75", {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                body: JSON.stringify({
                    name: "FormSubmit",
                    message: JSON.stringify(this.state.map.moreHexas[0])
                })
            })
                .then(response => response.json())
                .then(data => console.log(data))
                .catch(error => console.log(error));
        */
    }
    receiveNewMap = (response) => {
        const [newHexas, lstTile] = generateHexes(response)
        const newRivers = generateRivers(newHexas)
        this.setState({map: {...this.state.map, moreHexas: newHexas, moreRivers: newRivers}, lstTile})
    }

    calculateCosts = () => {
        let totalUt = 0;
        let totalUb = 0;
        {console.log("ACTIONS CHOISIES : ", this.state.actions)}
        /*this.state.actions.map(item => {
            if(item !== null){
                totalUb += item.cost.ub;
                totalUt += item.cost.ut;
            }
        })*/
        //console.log("costs = ", this.state.actions.cost)
        for(const index in this.state.actions.cost){
            //console.log(this.state.actions.cost[index])
            totalUt += this.state.actions.cost[index].ut;
            totalUb += this.state.actions.cost[index].ub;
        }
        //console.log("RESOURCES UT = ", this.state.resources[0])
        //console.log("RESOURCES UB = ", this.state.resources[1])
        //console.log("UT = " + totalUt + " UB = " + totalUb)
        return [totalUt, totalUb]
    }

    handleSubmit = () => {
        let totalUt = 0;
        let totalUb = 0;
        {console.log("ACTIONS CHOISIES : ", this.state.actions)}
        /*this.state.actions.map(item => {
            if(item !== null){
                totalUb += item.cost.ub;
                totalUt += item.cost.ut;
            }
        })*/
        //console.log("costs = ", this.state.actions.cost)
        for(const index in this.state.actions.cost){
            //console.log(this.state.actions.cost[index])
            totalUt += this.state.actions.cost[index].ut;
            totalUb += this.state.actions.cost[index].ub;
        }
        //console.log("RESOURCES UT = ", this.state.resources[0])
        //console.log("RESOURCES UB = ", this.state.resources[1])
        //console.log("UT = " + totalUt + " UB = " + totalUb)
        totalUt = this.state.resources[0] - totalUt;
        totalUb = this.state.resources[1] - totalUb
        //console.log("new UT = " + totalUt + " new UB = " + totalUb)
        /*this.setState({
            totalUt: totalUt,
            totalUb: totalUb
        })*/
        //console.log("new UT = " + this.state.totalUt + " new UB = " + this.state.totalUb)
        //console.log("FINAL TOTAL UT " + this.state.totalUt + " UB " + this.state.totalUb)
        socket.emit("setPlayersResources", totalUt, totalUb)
        this.setState({interfaceCtx: 4})
    }

    /*
        Function : componentDidMount

        Description : subscribe to the required function from server to keep up to date

        Author : Hugo KELHETTER

    */
    componentDidMount() {
        socket.emit("getTurn", (response) => {
            this.setState({tour: response})
        })

        socket.emit("getTimer", 3,(response) => {
            console.log(response);
            console.log(calculateTime(response));
            this.setState({timer: calculateTime(response)});
            console.log("State : ", this.state.timer);
        })

        socket.emit("getStats", this.props.name, (response) => {
            console.log(response[0]);
            const resources = [response[0].ut, response[0].ub]
            const competences = [response[0].GCultures, response[0].Verger,
                response[0].BovinV, response[0].Viticulture, response[0].BovinL, response[0].Granivore];
            //this.setState({competences, resources})
            this.setState({competences, resources})
            //console.log(calculateTime(response));
            //this.setState({timer: calculateTi, me(response)});
            //console.log("State : ", this.state.timer);
        })

        /*socket.emit("getPlayerActivity", this.state.id, (response) => {
            console.log("GET PLAYER ACTIVITY IS : ", response);
        })*/


        socket.on("disconnect", () => {
            this.setState({disconnected: true});
        })

        socket.on("connect", () => {
            if (this.state.disconnected) {
                socket.emit('reconnect', this.props.room, this.props.name, () => {
                    this.setState({disconnected: false})
                });
            }
        })

        if (this.props.role < 10) {
            socket.emit("updateStats", (response) => {
                this.setState({ressources: response[0]})
            })
        }

        socket.on("results", (response) => {
            this.setState({
                ressources: response.stats,
                data: response.graph,
                fini: false,
                displaySlider: true,
                inputPhase: false
            })
            socket.emit("getTurn", (response) => {
                this.setState({tour: response})
            })
            this.setState({actions: {}})
            socket.emit("getCurrentGrid", (response) => {
                this.receiveNewMap(response)
            })
            //    this.setState({ ressources: response[0] })

        })
        /*  socket.emit("createRoom", "player1", 0, (responseCreateRoom) => {
             this.setState({ room: responseCreateRoom })
             socket.emit("startGame")*/
        socket.on("inputPhase", () => {
            console.log("aaaaaaaa")
            socket.emit("getCurrentGrid", (response) => {
                this.receiveNewMap(response)
                /*             const [newHexas, lstTile] = generateHexes(response)
                            const newRivers = generateRivers(newHexas)
                            this.setState({ map: { ...this.state.map, moreHexas: newHexas, moreRivers: newRivers }, lstTile })
                 */
            })
            this.setState({inputPhase: true})

        })
        socket.emit("getCurrentGrid", (response) => {
            this.receiveNewMap(response)
            /*             const [newHexas, lstTile] = generateHexes(response)
                        const newRivers = generateRivers(newHexas)
                        this.setState({ map: { ...this.state.map, moreHexas: newHexas, moreRivers: newRivers }, lstTile })
             */
        })
        socket.emit("getAllActions", (response) => {
            this.setState({lstActions: response})
        })
        socket.emit("playersInRoom", (response) => {
            const player = response.filter((player, i) => player.Name === this.props.name)
            this.setState({lstPlayer: response, id: player[0].Id})
            //})
        })
    }

    componentWillUnmount() {
        socket.removeAllListeners()
    }


    endRound = () => {
        this.setState({fini: true, action: {}})
    }

    /* 
        Function : render

        Syntax  : render()

        Description : display the different components of the app

        Author : Hugo KELHETTER
    */
    closeDiary = () => {
        this.setState({displayDiary: false})
    }

    openTuto() {
        window.open(`${window.location.href}tutorial?tuto=3`)
    }

    displayDiary = () => {
        this.setState({displaySlider: false, displayDiary: true})
    }

    displaySlideField() {
        return <SlideField name={this.props.name} displayDiary={this.displayDiary}/>
    }

    actionChoice() {
        let costs = this.calculateCosts();
        //console.log(this.state.inputPhase)
        return (<>
            <Header tour={this.state.tour}
                    role={this.props.role}
                    pseudo={this.props.name}
                    bassin={nameSubBassin(getSubBassin(this.state.id))}
                    numeroPhase={3}
                    h={this.context.timerActions[0]}
                    m={this.context.timerActions[1]}
                    s={this.context.timerActions[2]}
                    finishEvent={null}/>
            <div className="row">
                <div className="col-sm-2">
                    <MenuRight ut={23} ub={33} formations={[]} role={this.props.role} pseudo={this.props.name}/>
                </div>
                <div className="col-sm-10">
                    {<>
                            {Object.keys(this.state.lstConvo).length > 0 ? <Chat lstConvo={this.state.lstConvo}/> : ""}
                            <div className="row">
                                <div className="col-sm-8">
                                    <section>
                                        {
                                            this.state.map.moreHexas !== "" && <Bassin handleClick={this.handleClickTile}
                                                                                       map={this.state.map}
                                                                                       role={this.props.role}
                                                                                       selectedId={this.state.selectedTile?.id}
                                                                                       id={this.state.id}/>
                                        }
                                    </section>
                                </div>
                                <div className="col-sm-4">
                                    {<>
                                        {console.log(this.state.actions)}
                                            <DetailsParcelle id={this.state.selectedTile?.practice}
                                                             currentActivity={this.state.lstActions[this.state.selectedTile?.practice - 101]?.Pratique}/>
                                            {this.state.selectedTile === null ? null :
                                                <section className={StylePratique.classNameSection} onClick={() => {
                                                    this.setState({interfaceCtx: 0})
                                                }}>
                                                    <p className="text-center">Choisir une activité</p>
                                                </section>
                                            }
                                        <div className="text-center">
                                            {/*costs = this.calculateCosts()*/}
                                            <section className={StylePratique.classNameSection}>
                                                <header>
                                                    Dépense de ce tour
                                                </header>
                                                <p>UT : {costs[0]}</p>
                                                <p>UB : {costs[1]}</p>
                                            </section>
                                        </div>
                                            <button onClick={this.handleSubmit}>Next</button>
                                    </>
                                    }
                                </div>
                            </div>
                        </>
                    }
                </div>
            </div>
        </>)
    }

    /*
        author : Hossam ELOUATI
     */

    /*< div className="App" >
                    {this.state.displayDiary &&
                        <Diary nom={this.props.name} data={this.state.data} closeDiary={this.closeDiary} />}
                    <Menu >
                        {this.state.inputPhase ?
                            <div id="menu">
                                <Button variant="contained" color="primary" onClick={this.openTuto}>Aide</Button>
                                <p>Nous sommes au tour : {this.state.tour}</p>
                                <p>Bonjour {this.props.name}. Vous êtes {roleToString(this.props.role)}, votre identifiant est {this.state.id}</p>
                                {this.state.ressources !== undefined && <Ressources ressources={this.state.ressources} cost={this.state.cost} />}
                                {!this.state.fini ?
                                    <>
                                        {console.log("SELECTED TILE = ", this.state.selectedTile)}
                                        {this.state.selectedTile === null ? "" :
                                            <ActivitySwapper key="changeActivity" changeTileActivity={this.changeTileActivity}
                                                selectedTile={this.state.selectedTile} actions={this.state.lstActions} />
                                        }
                                        {console.log("LIST ACTIONS = ", this.state.lstActions)}
                                        <ValidationTour key="validation" endRound={this.endRound} tour={this.state.tour} actions={this.state.actions} />
                                    </>
                                    : " En attente des autres joueurs..."}
                            </div> : "L'animateur est en train de modifier la carte"}
                    </Menu>
                    {Object.keys(this.state.lstConvo).length > 0 ? <Chat lstConvo={this.state.lstConvo} /> : ""}
                    <div className="row">
                        <div className="col-sm-6">
                            {
                                this.state.map.moreHexas !== "" && <Bassin handleClick={this.handleClickTile}
                                                                           map={this.state.map} role={this.props.role} selectedId={this.state.selectedTile?.id} id={this.state.id} />
                            }
                        </div>
                        <div className="col-sm-6">
                            <DetailsParcelle id={this.state.selectedTile?.practice} currentActivity={this.state.selectedTile?.name}/>
                        </div>
                    </div>


                </div >*/


    pratiques() {
        return (
            <Pratiques onClickEvent={() => {
                this.setState({interfaceCtx: 3})
            }} changeTileActivity={this.changeTileActivity} selectedTile={this.state.selectedTile}
                       actions={this.state.lstActions} role={this.props.role} pseudo={this.props.name}
                        competences={this.state.competences}/>
        )
    }

    updating() {
        return (
            <Updating ut={23} ub={3} oldUb={-1} oldUt={0} formations={[1, 0, 9, 1]} role={this.props.role}
                      pseudo={this.props.name} bassin={nameSubBassin(getSubBassin(this.state.id))}
                      tour={this.state.tour}
                      h={this.context.timerUpdating[0]}
                      m={this.context.timerUpdating[1]}
                      s={this.context.timerUpdating[2]}
                      onClickEvent={() => this.setState({interfaceCtx: 2})}/>
        )

    }

    strategy() {
        return (
            <Strategy h={0} m={2} s={10} role={this.props.role} listPlayers={["Hossam", "Rien", "Plus", "Au", "CR"]}
                      pseudo={this.props.name} bassin={nameSubBassin(getSubBassin(this.state.id))}
                      tour={this.state.tour} formations={[1, 0, 1, 1]}
                      ut={23} ub={23}
                      onClickEvent={() => this.setState({interfaceCtx: 3})}/>
        )
    }

    events() {
        return (
            <Events pseudo={this.props.name} bassin={nameSubBassin(getSubBassin(this.state.id))} tour={this.state.tour}
                    formations={[1, 0, 1, 1]} indicateurs={[123, 56]}
                    ut={23} ub={23} meteorology={[[0, 1], [2, 1], [2, 3]]} role={this.props.role} maladie={true}
                    onClickEvent={() => this.setState({interfaceCtx: 5})}/>
        )
    }

    debriefing() {
        return (
            <>
                <Debriefing h={0} m={2} s={10} role={this.props.role}
                            pseudo={this.props.name} bassin={nameSubBassin(getSubBassin(this.state.id))}
                            tour={this.state.tour}
                            onClickEvent={() => this.setState({interfaceCtx: 6})}/>
            </>
        )
    }

    finishTour = () => {
        socket.emit("finishTour", this.state.id, true, (response) => {
            console.log("Finish Tour : ", this.state.id);
        })
        //this.setState({interfaceCtx: 7});
    }

    satisfaction() {
        return (
            <Satisfaction role={this.props.role}
                     h={1} m={0} s={0}
                     pseudo={this.props.name}
                     bassin={nameSubBassin(getSubBassin(this.state.id))}
                     tour={this.state.tour} onClickEvent={this.finishTour} resultats={() => {this.setState({interfaceCtx: 7})}}/>
        )
    }

    results() {
        return (
            <Results role={this.props.role}
                          h={1} m={0} s={0}
                            data={this.state.data}
                          pseudo={this.props.name}
                          bassin={nameSubBassin(getSubBassin(this.state.id))}
                          tour={this.state.tour} onClickEvent={() => this.setState({interfaceCtx: 1}) }/>
        )
    }

    render() {
        console.log("CONTEXT : ", this.context);
        return (
            <>
                {
                    this.state.interfaceCtx === 1 ? this.updating() :
                        <div>
                            {this.state.interfaceCtx === 2 && this.strategy()}
                            {this.state.interfaceCtx === 3 && this.actionChoice()}
                            {this.state.interfaceCtx === 4 && this.events()}
                            {this.state.interfaceCtx === 5 && this.debriefing()}
                            {this.state.interfaceCtx === 6 && this.satisfaction()}
                            {this.state.interfaceCtx === 7 && this.results()}
                            {this.state.interfaceCtx === 0 && this.pratiques()}
                        </div>
                }
                {/*{console.log("STATE ID = " + this.state.id + "\nROLE = "+this.props.role)}
                <Updating ut={23} ub={3} oldUb={-1} oldUt={0} formations={[1, 0, 9, 1]} role={this.props.role} h={0} m={0} s={20}
                   pseudo={this.props.name} bassin={nameSubBassin(getSubBassin(this.state.id))} numeroPhase={1} tour={this.state.tour}/>);*/}
            </>)
    }
}
export default Conteneur
