import React, {Component} from 'react';
import Header from "../Header/Header";
import MenuRight from "../MenuRight/MenuRight";
import Style from "../Satisfaction/Satisfaction.module.scss";
import {socket} from "../../socket";
import calculateTime from "../utils/calculateTime";
import timersDefault from "../../Game/TimersDefault";

class Results extends Component {

    state={
        timer: []
    }

    componentDidMount() {
        socket.emit("getTimer", 7,(response) => {
            console.log(response);
            console.log(calculateTime(response));
            this.setState({timer: calculateTime(response)});
            console.log("State : ", this.state.timer);
        })}

    render() {
        return (
            <div>
                <Header tour={this.props.tour - 1} pseudo={this.props.pseudo} bassin={this.props.bassin}
                        role={this.props.role} numeroPhase={7}   h={(this.props.pTimer)? this.state.timer[0] : timersDefault(7)[0]}
                        m={(this.props.pTimer)? this.state.timer[1] : timersDefault(7)[1]}
                        s={(this.props.pTimer)? this.state.timer[2] : timersDefault(7)[2]}
                        finishEvent={this.props.onClickEvent}/>
                <div className="row">
                    <div className="col-sm-2">
                        <MenuRight pseudo={this.props.pseudo}
                                   role={this.props.role}/>
                    </div>
                    <div className="col-sm-10 text-center">
                        <section className={Style.section}>
                            <header>
                                <h2> RÃ©sultats du tour </h2>
                            </header>
                            <div>
                                <img src={this.props.data} className={Style.image} alt={"/"}/>
                            </div>
                        </section>
                        <button onClick={this.props.onClickEvent}>Next Turn</button>
                    </div>
                </div>
            </div>
        );
    }
}


export default Results;
