import React, {Component} from 'react';
import Style from "./Pratiques.module.scss";
import HexagonBovin from "./hexagons/hex_Bovin.png";
import HexagonVerger from "./hexagons/hex_verger.png";
import HexagonViti from "./hexagons/hex_viticulture.png";
import HexagonGC from "./hexagons/hex_grandesCultures.png";
import HexagonGranivore from "./hexagons/hex_granivore.png";
import HexagonLaitier from "./hexagons/hex_laitier.png";
import HexagonNormal from "./hexagons/hexagon.png";

class DetailsParcelle extends Component {
    renderPicture(identifier) {
        let id = parseInt(identifier);
        return ((id >= 101 && id <= 103) ? HexagonBovin :
                    (id >= 106 && id <= 111) ? HexagonVerger :
                        (id >= 112 && id <= 117) ? HexagonViti :
                            (id >= 104 && id <= 105) ? HexagonGC :
                                (id === 119 || id === 120) ? HexagonGranivore :
                                    (id === 118 ) ? HexagonLaitier :
                                        HexagonNormal
        )
    }

    render() {
        return (
            <div className="text-center">
                <header className={Style.NewTitle}>
                    <div className="text-center">
                        DÃ©tails de la Parcelle
                    </div>
                </header>
                <div className={Style.SubTitle}>
                        <img src={this.renderPicture(this.props.id)} alt={"."} className={Style.Hexagon} />
                </div>
                <div className={Style.SubTitle}>
                    Pratiques : {this.props.currentActivity}
                </div>
            </div>
        );
    }
}

export default DetailsParcelle;
