import { createContext, useState } from 'react';

const TimerContext = createContext({
    timerUpdating: [],
    timerStrategy: [],
    timerActions: [],
    timerEvents: [],
    timerDebriefing: [],
    timerSatisfaction: [],
    timerResults: [],
    updateUpdating: (timer) => { },
    updateStrategy: (timer) => { },
    updateActions: (timer) => { },
    updateEvents: (timer) => { },
    updateDebriefing: (timer) => { },
    updateSatisfaction: (timer) => { },
    updateResults: (timer) => { },
});

export function TimerContextProvider(props) {

    const [updating, setUpdating] = useState([]);
    const [strategy, setStrategy] = useState([]);
    const [actions, setActions] = useState([]);
    const [events, setEvents] = useState([]);
    const [debriefing, setDebriefing] = useState([]);
    const [satisfaction, setSatisfaction] = useState([]);
    const [results, setResults] = useState([]);

    function updateUpdatingHandler(timer) {
        setUpdating(timer);
    }
    function updateStrategyHandler(timer) {
        setStrategy(timer);
    }
    function updateActionsHandler(timer) {
        setActions(timer);
    }
    function updateEventsHandler(timer) {
        setEvents(timer);
    }
    function updateDebriefingHandler(timer) {
        setDebriefing(timer);
    }
    function updateSatisfactionHandler(timer) {
        setSatisfaction(timer);
    }
    function updateResultsHandler(timer) {
        setResults(timer);
    }

    const context = {
        timerUpdating: updating,
        timerStrategy: strategy,
        timerActions: actions,
        timerEvents: events,
        timerDebriefing: debriefing,
        timerSatisfaction: satisfaction,
        timerResults: results,
        updateUpdating: updateUpdatingHandler,
        updateStrategy: updateStrategyHandler,
        updateActions: updateActionsHandler,
        updateEvents: updateEventsHandler,
        updateDebriefing: updateDebriefingHandler,
        updateSatisfaction: updateSatisfactionHandler,
        updateResults: updateResultsHandler
    };

    return <TimerContext.Provider value={context}>
        {props.children}
    </TimerContext.Provider>
}

export default TimerContext;
