import React, {Component} from "react";
import StyleStrategy from "./Strategy.module.scss";
import ImageRDV from "./priseRDV.png";
import Header from "../Header/Header";
import MenuRight from "../MenuRight/MenuRight";
import {socket} from "../../socket";
import calculateTime from "../utils/calculateTime";

export default class Strategy extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pseudo: this.props.pseudo,
            role: this.props.role,
            ancient: (props.role === 0 ? "le gestionnaire" : "l'élu"),
            child: (props.role === 0) ? "agriculteurs" : "élus",
            timer: [],
            waitingPerson: 0,
            onClickCalling: false,
            isCalling: false,
            orderReady: false,
            available: true,
            availableList: false,
            callingList: ["", "", ""],
            toFinish: 0
        }

        setTimeout( () => {this.setState({orderReady: this.state.isCalling})}, 1000)
        const x = setInterval(() => {
            let y;
            if(this.state.timer.length && this.state.timer) {
                y = this.state.timer[0]*3600 + this.state.timer[1]*60 + this.state.timer[2] - this.state.toFinish;
            }
            else {
                y = 1;
            }
            //const y = this.state.finished - this.state.toFinish;
            if( y <= 0) {
                clearInterval(x);
            }
            this.setState({toFinish: this.state.toFinish + 10000});
            this.gettingList()
        }, 10000)
    }

    componentDidMount = () =>  {

        socket.emit("getTimer", 2,(response) => {
            console.log(response);
            console.log(calculateTime(response));
            this.setState({timer: calculateTime(response)});
            console.log("State : ", this.state.timer);
        })

        /*socket.emit("getOrderWaitingList", this.props.pseudo, this.props.role, (response) => {
            //this.setState({});
            setTimeout( () => {this.setState({available: true, waitingPerson: response})}, 1000)
            //console.log("Waiting = ", this.state.waitingPerson);
        })*/

        /*if(this.props.role === 0 || this.props.role === 2) {
            socket.emit("getCallingList", this.props.pseudo, (response) => {
                console.log("LIST = ", response);
                this.setState({callingList: response});
            })
        }*/
    }

    gettingList = () => {
        if (this.props.role === 0 || this.props.role === 2) {
            socket.emit("getCallingList", this.props.pseudo, this.props.tour, (response) => {
                console.log("LIST = ", response);
                this.setState({callingList: response});
                console.log("LIST CALLING = ", this.state.callingList);
            })
            this.setState({availableList: true});
        }
    }

    submit() {
        socket.emit("rdv", this.props.pseudo, this.props.role, this.props.tour, (response => {
            console.log("SUBMIT = ", response);
            this.setState({waitingPerson: response})
        }));
        this.setState({isCalling: true})
    }

    handleSubmit = () => {this.submit()}

    callingPlayers() {
        //setTimeout( () => {this.setState({availableList: true})}, 1000)
        return (<section>
            <header className={StyleStrategy.header2}>
                Les {this.state.child} vous appelent
            </header>
            <ul>
                {this.state.callingList.map((f, index) =>
                    <li key={index} className={StyleStrategy.callingPlayer}> <b>{f}</b> </li>
                )}
            </ul>
        </section>)
        /*return (
            <section>
                <header className={StyleStrategy.header2}>
                    Les {this.state.child} vous appelant
                </header>
                <ul>
                    {this.props.listPlayers.map((f, index) =>
                        (<li className={StyleStrategy.callingPlayer}> <b>{f}</b> </li>)
                    )}
                </ul>
            </section>
        )*/
    }

    renderWaiting() {
        if(this.state.isCalling) {
            return (<div className={StyleStrategy.prendreRDV}>
                        Vous êtes <b>{this.state.waitingPerson + 1}</b> ème en liste d'attente
                    </div>)
        } else {
            return (
                <div className={StyleStrategy.prendreRDV}>
                    <ul>
                        <li><img onClick={this.handleSubmit} src={ImageRDV} className={StyleStrategy.RDV} alt={"/"}/></li>
                        <li className={StyleStrategy.texte2}>Prendre un rendez-vous</li>
                    </ul>
                </div>
                )
        }

    }

    renderRDV() {
        /* ELU */
        if(this.props.role === 0) {
            return (
                <div className="row text-center">
                    <div className="col-sm-5 border-right border-dark">
                        {this.renderWaiting()}
                    </div>
                    <div className={"col-sm-7"}>
                        {this.callingPlayers()}
                    </div>
                </div>
            )
        } /* GESTIONNAIRE */
        else if(this.props.role === 2) {
            return (<div className="text-center">
                {this.callingPlayers()}
            </div>);
        } /* AGRI */
        else if(this.props.role === 1) {
            return (<div className="text-center">
                {this.renderWaiting()}
            </div>)
        }
        else {return null;}
    }

    render() {
        return (
            <>
                <Header tour={this.props.tour} pseudo={this.props.pseudo} bassin={this.props.bassin} role={this.props.role} numeroPhase={2}  h={this.state.timer[0]} m={this.state.timer[1]} s={this.state.timer[2]}
                        finishEvent={this.props.onClickEvent}/>
                <div className="row">
                    <div className="col-sm-2">
                        <MenuRight ut={this.props.ut} ub={this.props.ub} formations={this.props.formations} role={this.props.role} pseudo={this.props.pseudo}/>
                    </div>
                    <div className="col-sm-10">
                        <section className={StyleStrategy.none}>
                            <header className="text-center">
                                <div className={StyleStrategy.header}>
                                    Service de prise de rendez-vous
                                </div>
                            </header>
                            {this.renderRDV()}
                        </section>
                        <button onClick={this.props.onClickEvent} className={"btn btn-small btn-primary"}>Next</button>
                    </div>
                </div>
            </>

        )
    }
}
