import React, {Component} from 'react';
import Style from "../Header/Header.module.scss";
import StyleStrategy from "../Strategy/Strategy.module.scss";
import Checkbox from "../utils/Checkbox";
import {socket} from "../../socket";

class Settings extends Component {

    state = {
        selected: new Set(),
        personalized: this.selectedCheckboxes.has(" 1.")
    }

    componentWillMount = () => {
        this.selectedCheckboxes = new Set();
    }

    toggleCheckbox = (label) => {
        const newSelected = new Set();
        if (this.selectedCheckboxes.has(label)) {
            //this.setValueActivity(label, 0);
            this.selectedCheckboxes.delete(label);
            this.setState({selected: newSelected});

        } else {
            this.selectedCheckboxes.add(label);
            this.setState({selected: newSelected});
        }
    }

    onClickEvent = () => {
        //this.handleSubmit();
        this.props.nextInterface();
    }

    handleFormSubmit = formSubmitEvent => {
        formSubmitEvent.preventDefault();

        for (const checkbox of this.selectedCheckboxes) {
            console.log(checkbox, 'is selected.');
        }
    }

    createCheckbox = (label) => (
        <>
            <label htmlFor={"agri"}>
                <Checkbox label={label} isChecked={ this.selectedCheckboxes.has(label)} handleCheckboxChange={this.toggleCheckbox} key={label}/>
            </label>
        </>
    )
    render() {
        return (
            <>
                <div className={Style.Header}>
                    <div className={Style.Content}>
                        <div className="text-center">
                            <div className={Style.TextColor}>
                                Jeu Sérieux - Le Bassin de Moderbach
                            </div>
                        </div>
                        <div className={Style.TextColor}>
                            <div className="row text-center">
                                <div className="col-sm-6">Pseudo : {this.props.pseudo}</div>
                                <div className="col-sm-6">Rôle : Animateur</div>
                            </div>
                        </div>
                    </div>
                </div>
                <div>
                    <div className="text-center">
                        <button className={StyleStrategy.nextInterface} onClick={this.onClickEvent}>
                            Passer à l'interface suivante
                        </button>
                    </div>
                    <section className={StyleStrategy.none4}>
                        <header className="text-center">
                            <div className={StyleStrategy.header}>
                                Choisir entre ces options
                            </div>
                        </header>
                        <section className="row text-center">
                            <div className={StyleStrategy.header2}>
                                {this.createCheckbox(" 1.")} Personnaliser les rôles des agriculteurs
                                <br/>
                                {this.createCheckbox(" 2.")} Personnaliser les durées de chaque phase
                                <br/>
                                {(!this.state.personalized)
                                (<>
                                    {this.createCheckbox(" 3.")} Choisir un nombre pour chaque type d'agriculteurs}
                                    <br/>
                                    {this.createCheckbox(" 4.")} Définir les types d'agriculteurs aléatoirement :}
                                    </>)} : null}

                            </div>
                        </section>
                        {console.log(this.selectedCheckboxes)}
                    </section>
                </div>
            </>
        );
    }
}

export default Settings;
