import React, {Component} from 'react';
import Style from "./Pratiques.module.scss";
import AgroEcology from "./Actions/agroecologique.png";
import Brigade from "./Actions/Badge_BrigadeVerte.png";
import Bail from "./Actions/bail_envir.png";
import Equipement from "./Actions/equipement.png";
import Expertise from "./Actions/expertise_scientifique.jpg";
import Formation from "./Actions/formation.png";
import Infrastructure from "./Actions/infrastructure.png";
import Market from "./Actions/marché.jpg";
import Plu from "./Actions/plu.png";
import Reglement from "./Actions/reglement.png";
import RenaturationEau from "./Actions/renaturation_eau.png";
import Assainissement from "./Actions/reseau_assainissement.png";
import ReunionCle from "./Actions/reunion_cle.png";

import createCheckbox from "../../Game/controls/createCheckbox";
import PropTypes from "prop-types";
import {socket} from "../../socket";
import MenuRight from "../MenuRight/MenuRight";

class ActionsAutres extends Component {

    constructor(props) {
        super(props);
        this.state = { selectActivity: 0, checkbox: false };
        this.createCheckbox = createCheckbox.bind(this)
    }

    static propTypes = {
        changeTileActivity: PropTypes.func.isRequired,
        selectedTile: PropTypes.object.isRequired,
        actions: PropTypes.array.isRequired
    }

    /*
        Function : handleChange

        Syntax  : handleChange(event)

        Input   : event : the event calling the function

        Description :
            function called when interacting with the form's components
            if the target is a checkBox, change its validation
            update the state value for the corresponding event target

        Author : Hugo KELHETTER
    */
    handleChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        this.setState({ [target.name]: value });
    }
    /*
        Function : handleSubmit

        Syntax  : handleSubmit(event)

        Input   : event :  the event calling the function

        Description :
            call the function changeTileActivity from parent with in parameters the values of the fieldset

        Author : Hugo KELHETTER
    */
    handleSubmit = () => {
        //event.preventDefault();
        if (this.state.selectActivity !== 0) {
            console.log("SELECT ACTIVITY : ", this.state.selectActivity)
            //this.props.changeTileActivity(this.props.actions[this.state.selectActivity], this.state.checkbox)
            //console.log("ACTIVITY FOR ", this.props.selectedTile)
            let pratique = this.props.actions[this.state.selectActivity - 101];
            this.props.changeTileActivity(pratique, this.state.checkbox);
            /*socket.emit("setPractise", this.state.selectActivity, this.props.selectedTile.id, (response => {
                console.log("SUBMIT = ", response);
            }));*/
            //console.log(pratique.Intrants)
        }
        this.props.onClickEvent();
    }


    cardPratiques(image, titre, cost, prod, pollution, activityId) {
        // cost = [Travail, Fonctionnement, Investissement, Entretien]
        //let classNameVar = (cost[2] > currentCompetence) ? Style.UnavailableCard : classNameSection;
        return (
            <div className={Style.classNameSection} onClick={() => this.setState({selectActivity: activityId})}>
                <section className={(cost[2] > this.props.competences[currentCompetence]) ? Style.UnavailableCard : Style.AutresActionsCard}>
                    <div className="row">
                        <div className="col-sm-4">
                            <img src={(cost[2] > this.props.competences[currentCompetence])? Unavailable: image} alt={"/"} className={Style.BovinViande}/>
                        </div>
                        <div className="col-sm-8">
                            <div className="text-center">
                                <p className={Style.Title}>{titre}</p>
                                <p className={Style.SubTitle}>{sousTitre}</p>
                                <p>Travail : {cost[0]} UT</p>
                                <p>Fonctionnement : {cost[1]} UB</p>
                                <p>Investissement : {cost[2]} UB</p>
                                <p>Entretien : {cost[3]} UB</p>
                                <p>Production : {prod}</p>
                                <p>Pollution : {pollution}</p>
                            </div>

                        </div>
                    </div>
                </section>
            </div>
        )
    }

    render() {
        {console.log("COMPETENCES = ", this.props.competences)}
        return (
            <>
                <div className="row">
                    <div className="col-sm-3">
                        <MenuRight pseudo={this.props.pseudo} role={this.props.role} onClickBank={null}/>
                    </div>
                    <div className="col-sm-9">
                        <div className="row">
                            <div className="col-sm-4">
                                {this.cardPratiques(Assainissement, "Investissement Réseau Assainissement", [4, 0, 10, 0], 0, 0, 202)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(Infrastructure, "Investissement Infrastructure", [4, 0, 10, 0], 0, 0, 303)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(AgroEcology, "Infrastructure Agroécologique", [2, 0, 3, 0], 0, 0, 11)}
                            </div>

                        </div>

                        <div className="row">
                            <div className="col-sm-4">
                                {this.cardPratiques(Bail, "Bail Environnemental", [3, 0, 2, 0], 0, 0, 52)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(Brigade, "Brigades Vertes", [2, 1, 2, 0], 0, 0, 53)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(Expertise, "Expertise scientifique", [1, 0, 2, 0], 0, 0, 306)}
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-sm-4">
                                {this.cardPratiques(RenaturationEau, "Renaturation Cours d'eau", [4, 0, 5, 0], 0, 0, 307)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(Market, "Marché des producteurs", [1, 0, 2, 1], 0, 0, 402)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(Reglement, "Révision Règlement", [3, 0, 1, 0], 0, 0, 51)}
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-sm-4">
                                {this.cardPratiques(Plu, "Révision PLU", [3, 0, 1, 0], 0, 0, 401)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(Formation, "Formation", [0, 0, 0, 0], 0, 0, 203)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(Equipement, "Investissement Équipement", [0, 0, 0, 0], 0, 0, 204)}
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-sm-4">
                                {this.cardPratiques(ReunionCle, "Réunion Clé", [1, 0, 0, 0], 0, 0, 301)}
                            </div>
                            <div className="col-sm-4">
                                <button onClick={this.handleSubmit}>Retour</button>
                            </div>
                        </div>
                    </div>
                </div>

            </>

        );
    }
}

export default ActionsAutres;
