import React, {Component} from "react";
import roleToString from "../utils/roleToString";
import Style from "./Header.module.scss";
import phaseToString from "../utils/phaseToString";
import MeetingTime from "../utils/MeetingTime";

export default class Header extends Component {
    constructor(props) {
        super(props);
        this.state = {
            colonnes: (this.props.role !== 2) ? "col-sm-3" : "col-sm-4",
            isOn: false
        }
        setTimeout( () => {this.setState({isOn: true})}, 1000)
    }


    render() {
        return (
            <div className={Style.Header}>
                <div className="row">
                    <div className="col-sm-2">
                        <div className={Style.Content}>
                            <div className={Style.Time}>
                                <MeetingTime hours={this.props.h} minutes={this.props.m} seconds={this.props.s} isOn={this.state.isOn}
                                    finishEvent={this.props.finishEvent}/>
                            </div>
                        </div>
                    </div>
                    <div className="col-sm-10">
                        <div className={Style.Content}>
                            <div className={Style.TextColor}>
                                <div className="row text-center">
                                    <div className="col-sm-6">
                                        Jeu Sérieux - Le Bassin de Moderbach
                                    </div>
                                    <div className="col-sm-6">
                                        Phase {this.props.numeroPhase} : {phaseToString(this.props.numeroPhase)}
                                    </div>
                                </div>
                            </div>
                            <div className={Style.TextColor}>
                                <div className="row text-center">
                                    <div className={this.state.colonnes} >Tour : {this.props.tour}</div>
                                    <div className={this.state.colonnes} >Pseudo : {this.props.pseudo}</div>
                                    <div className={this.state.colonnes} >Rôle : {roleToString(this.props.role)}</div>
                                    {this.props.role !== 2 ? <div className={this.state.colonnes} >Bassin : {this.props.bassin}</div> : null}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
