export default function meteoToString(meteo) {
    return (
        (meteo === 0) ?  "Chaud & Sec" :
            (meteo === 1) ? "Chaud & Humide" :
                (meteo === 2) ? "Froid & Sec" :
                    "Froid & Humide"
    );
}
