/*
    author : Hossam ELOUATI
 */

import { createContext, useState } from 'react';

const InterfaceContext = createContext({
    loc: "updating",
    updateLocation: (loc) => {}
});

export function InterfaceContextProvider(props) {

    const [location, setLocation] = useState("updating");

    function updateLocationHandler(loc) {
        setLocation(loc);
    }

    const context = {
        loc: location,
        updateLocation: updateLocationHandler
    }

    return <InterfaceContext.Provider value={context}>
        {props.children}
    </InterfaceContext.Provider>
}

export default InterfaceContext;
