import React, {Component} from 'react';
import Style from "./Pratiques.module.scss";
import GrandesCultures from "./Cultures/grandes_cultures.png";
import Verger from "./Cultures/verger.webp";
import BovinViande from "./Cultures/bovinViande.png";
import BovinLaitier from "./Cultures/bovinLaitier.png";
import Viticulture from "./Cultures/viticulture.webp";
import Granivore from "./Cultures/granivore.png";
import createCheckbox from "../../Game/controls/createCheckbox";
import PropTypes from "prop-types";
import {socket} from "../../socket";
import MenuRight from "../MenuRight/MenuRight";

class Pratiques extends Component {


    constructor(props) {
        super(props);
        this.state = { selectActivity: 0, checkbox: false };
        this.createCheckbox = createCheckbox.bind(this)
    }

    static propTypes = {
        changeTileActivity: PropTypes.func.isRequired,
        selectedTile: PropTypes.object.isRequired,
        actions: PropTypes.array.isRequired
    }

    /*
        Function : handleChange

        Syntax  : handleChange(event)

        Input   : event : the event calling the function

        Description :
            function called when interacting with the form's components
            if the target is a checkBox, change its validation
            update the state value for the corresponding event target

        Author : Hugo KELHETTER
    */
    handleChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        this.setState({ [target.name]: value });
    }
    /*
        Function : handleSubmit

        Syntax  : handleSubmit(event)

        Input   : event :  the event calling the function

        Description :
            call the function changeTileActivity from parent with in parameters the values of the fieldset

        Author : Hugo KELHETTER
    */
    handleSubmit = () => {
        //event.preventDefault();
        if (this.state.selectActivity != null) {
            //console.log("SELECT ACTIVITY : ", this.state.selectActivity)
            //this.props.changeTileActivity(this.props.actions[this.state.selectActivity], this.state.checkbox)
            //console.log("ACTIVITY FOR ", this.props.selectedTile)
            let pratique = this.props.actions[this.state.selectActivity - 101];
            this.props.changeTileActivity(pratique, this.state.checkbox);
            //console.log(pratique.Intrants)
        }
        this.props.onClickEvent();
        socket.emit("setPractise", this.state.selectActivity, this.props.selectedTile.id, (response => {
            console.log("SUBMIT = ", response);
        }));
    }


    cardPratiques(image, titre, sousTitre, cost, prod, pollution, classNameImage, classNameSection, activityId, currentCompetence) {
        //let classNameVar = (cost[2] > currentCompetence) ? Style.UnavailableCard : classNameSection;
        return (
            <div className={Style.classNameSection} onClick={() => this.setState({selectActivity: activityId})}>
                <section className={(cost[2] > currentCompetence) ? Style.UnavailableCard : classNameSection}>
                            <div className="row">
                                <div className="col-sm-4">
                                    <img src={image} alt={"/"} className={classNameImage}/>
                                </div>
                                <div className="col-sm-8">
                                    <div className="text-center">
                                        <p className={Style.Title}>{titre}</p>
                                        <p className={Style.SubTitle}>{sousTitre}</p>
                                        <p>Coût : <b>{cost[0]}</b> UT & <b>{cost[1]}</b> UB</p>
                                        <p>Niveau de compétence : {cost[2]}</p>
                                        <p>Production : {prod}</p>
                                        <p>Pollution : {pollution}</p>
                                    </div>

                                </div>
                            </div>
                </section>
            </div>
        )
    }

    render() {
        return (
            <>
                <div className="row">
                    <div className="col-sm-3">
                        <MenuRight pseudo={this.props.pseudo} role={this.props.role} onClickBank={null}/>
                    </div>
                    <div className="col-sm-9">
                        <div className="row">
                            <div className="col-sm-4">
                                {this.cardPratiques(BovinViande, "Bovin Viande", "Hors-Sol", [3,4,4], 5, 3, Style.BovinViande, Style.BovinViandeCard, 101, this.props.competences.BovinV)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(BovinViande, "Bovin Viande", "50% Pâturage", [3,2,2], 3, 2, Style.BovinViande, Style.BovinViandeCard, 102, this.props.competences.BovinV)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(BovinViande, "Bovin Viande", "100% Pâturage", [3,1,2], 2, 0, Style.BovinViande, Style.BovinViandeCard, 103, this.props.competences.BovinV)}
                            </div>

                        </div>

                        <div className="row">
                            <div className="col-sm-4">
                                {this.cardPratiques(GrandesCultures, "Grandes Cultures", "Conventionnel", [3,4,3], 5, 3, Style.BovinViande, Style.GrandesCulturesCard, 104, this.props.competences.GCultures)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(GrandesCultures, "Grandes Cultures", "Conventionnel Irrigué", [3,4,3], 5, 3, Style.BovinViande, Style.GrandesCulturesCard, 105, this.props.competences.GCultures)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(Verger, "Verger", "Conventionnel", [3,3,1], 5, 3, Style.BovinViande, Style.VergerCard, 106, this.props.competences.Verger)}
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-sm-4">
                                {this.cardPratiques(Verger, "Verger", "Conventionnel Irrigué", [3,3,1], 5, 3, Style.BovinViande, Style.VergerCard, 107, this.props.competences.Verger)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(Verger, "Verger", "Raisonné", [4,2,4], 4, 2, Style.BovinViande, Style.VergerCard, 108, this.props.competences.Verger)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(Verger, "Verger", "Raisonné Irrigué", [4,2,4], 4, 2,Style.BovinViande, Style.VergerCard, 109, this.props.competences.Verger)}
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-sm-4">
                                {this.cardPratiques(Verger, "Verger", "Bio", [5,1,5], 3, 1, Style.BovinViande, Style.VergerCard, 110, this.props.competences.Verger)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(Verger, "Verger", "Bio Irrigué", [5,1,5], 3, 1, Style.BovinViande, Style.VergerCard, 111, this.props.competences.Verger)}
                            </div>
                            <div className="col-sm-4">
                                {this.cardPratiques(Viticulture, "Viticulture", "Conventionnel", [3,3,1], 5, 3, Style.BovinViande, Style.ViticultureCard, 112, this.props.competences.Viticulture)}
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-sm-4">
