import React, {Component} from "react";

export default class Updating extends Component {
    formationId(index) {
        switch (index) {
            case 0:
                return "Polyculture";
            case 1:
                return "Verger";
            case 2:
                return "Maréchage";
            default:
                return "Bauvage";
        }
    }

    render() {
        return (
            <div>
                <h2> --------------------------- Body : Updating -----------------------------------</h2>
                <h3>Nouvelle valeur UT = {this.props.ut}</h3>
                <h3>Ancienne valeur UT = {this.props.oldUt}</h3>
                <h3>Nouvelle valeur UB = {this.props.ub}</h3>
                <h3>Ancienne valeur UB = {this.props.oldUb}</h3>
                {this.props.role === 1 ? <h3>Formations = {this.props.formations.map((f, index) =>
                    {if (f !== 0){
                        return (<div>
                                    <h3>{this.formationId(index)} = {f}</h3>
                                </div>  )
                    }}
                )}</h3> : null}
            </div>
        )
    }
}
