import React, {Component} from 'react';
import Style from "../Header/Header.module.scss";
import MeetingTime from "../utils/MeetingTime";
import phaseToString from "../utils/phaseToString";
import roleToString from "../utils/roleToString";
import StyleStrategy from "../Strategy/Strategy.module.scss";
import Checkbox from "../utils/Checkbox";
import FarmerActivity from "./FarmerActivity";
import defaultSettings from "../utils/defaultSettings";

class SetFarmers extends Component {

    state = {
        bassins: ["Amont", "Milieu", "Aval"],
        agriculteurs: [],
        defaultOption: this.props.default,
        valueButton: 0
    }

    increments() {
        //this.setState({valueButton: this.state.valueButton+1})
        document.getElementById('inc').value = ++this.state.valueButton;
    }
    selectAgri() {
        return (
            <>
                <button onClick={this.increments}>+</button>
                <input type="text" id="inc" value="0"/>
            </>
        )
    }

    render() {
        return (
            <>
                <div className={Style.Header}>
                    <div className={Style.Content}>
                        <div className="text-center">
                            <div className={Style.TextColor}>
                                Jeu Sérieux - Le Bassin de Moderbach
                            </div>
                        </div>
                        <div className={Style.TextColor}>
                            <div className="row text-center">
                                <div className="col-sm-6">Pseudo : {this.props.pseudo}</div>
                                <div className="col-sm-6">Rôle : Animateur</div>
                            </div>
                        </div>
                    </div>
                </div>
                <div>
                    <div className={StyleStrategy.nextInterface} onClick={this.props.nextInterface}>
                        <div className="text-center">
                            Passer à l'interface suivante
                        </div>
                    </div>
                    <section className={StyleStrategy.none}>
                        <header className="text-center">
                            <div className={StyleStrategy.header}>
                                Niveaux de compétences pour les agriculteurs
                            </div>
                        </header>
                        <section>
                            <header className="text-center">
                                <div className={StyleStrategy.header}>
                                    Bassin Amont
                                </div>
                            </header>
                            {this.props.listAgri.map(
                                x => <FarmerActivity x={x}/>)}
                        </section>
                        {this.selectAgri()}
                    </section>
                </div>
            </>
        );
    }
}

export default SetFarmers;
